import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/delivery-schema.ts.content:
// Comprehensive delivery management schema for complex oil delivery scenarios

export interface LoadSession {
  id: string;
  loadDriverId: string;
  loadDriverName: string;
  oilTypeId: string;
  oilTypeName: string;
  totalLoadedLiters: number;
  remainingLiters: number;
  loadTimestamp: string;
  loadLocationId?: string;
  loadMeterReading: number;
  loadPhoto: string;
  truckId?: string;
  truckPlateNumber?: string;
  status: 'active' | 'completed' | 'cancelled';
  createdAt: string;
  updatedAt: string;
}

export interface DeliveryOrder {
  id: string;
  orderNumber: string;
  loadSessionId: string; // Links to the load session
  deliveryDriverId: string;
  deliveryDriverName: string;
  branchId: string;
  branchName: string;
  branchAddress: string;

  // Delivery details
  requestedLiters: number;
  actualDeliveredLiters: number;
  startMeterReading: number;
  endMeterReading: number;

  // Photos and evidence
  tankLevelBeforePhoto: string;
  hoseConnectionPhoto: string;
  tankLevelAfterPhoto: string;
  deliveryReceiptPhoto?: string;

  // Timestamps
  scheduledDeliveryTime?: string;
  actualDeliveryStartTime: string;
  actualDeliveryEndTime: string;

  // Status tracking
  status: 'pending' | 'in_progress' | 'completed' | 'cancelled';

  // Additional data
  customerSignature?: string;
  deliveryNotes?: string;
  complaintId?: string;

  createdAt: string;
  updatedAt: string;
}

export interface DeliveryTransaction {
  id: string;
  loadSessionId: string;
  deliveryOrderId: string;
  transactionType: 'delivery' | 'return' | 'transfer';

  // Driver info
  loadDriverId: string;
  deliveryDriverId: string;

  // Oil details
  oilTypeId: string;
  litersTransferred: number;

  // Location details
  fromLocationId?: string; // For transfers
  toLocationId: string; // Branch or depot

  // Meter readings
  meterBefore: number;
  meterAfter: number;

  // Evidence
  photos: string[];

  timestamp: string;
  createdAt: string;
}

export interface DeliveryComplaint {
  id: string;
  loadSessionId?: string;
  deliveryOrderId?: string;

  // Reporter info
  reportedByDriverId: string;
  reportedByDriverName: string;

  // Complaint details
  complaintType: 'equipment' | 'quality' | 'quantity' | 'customer' | 'safety' | 'other';
  severity: 'low' | 'medium' | 'high' | 'critical';
  description: string;

  // Location and context
  branchId?: string;
  location: string;

  // Evidence
  photos: string[];

  // Resolution
  status: 'open' | 'investigating' | 'resolved' | 'closed';
  resolutionNotes?: string;
  resolvedByAdminId?: string;
  resolvedAt?: string;

  createdAt: string;
  updatedAt: string;
}

// Summary interfaces for reporting
export interface LoadSessionSummary {
  loadSession: LoadSession;
  totalDeliveries: number;
  totalDeliveredLiters: number;
  remainingLiters: number;
  deliveries: DeliveryOrder[];
  complaints: DeliveryComplaint[];
}

export interface DriverDayReport {
  driverId: string;
  driverName: string;
  date: string;

  // Load sessions (if driver loaded)
  loadSessions: LoadSession[];
  totalLoaded: number;

  // Deliveries (if driver delivered)
  deliveries: DeliveryOrder[];
  totalDelivered: number;

  // Summary
  branchesServiced: string[];
  complaints: DeliveryComplaint[];
  hoursWorked: number;
}

export interface AdminDashboardData {
  date: string;

  // Overall metrics
  totalLoadSessions: number;
  totalLitersLoaded: number;
  totalDeliveries: number;
  totalLitersDelivered: number;
  efficiency: number; // delivered/loaded ratio

  // Driver performance
  activeDrivers: number;
  driverReports: DriverDayReport[];

  // Branch metrics
  branchDeliveries: {
    branchId: string;
    branchName: string;
    deliveryCount: number;
    totalLiters: number;
  }[];

  // Issues
  openComplaints: DeliveryComplaint[];
  pendingDeliveries: DeliveryOrder[];

  // Oil type breakdown
  oilTypeMetrics: {
    oilTypeId: string;
    oilTypeName: string;
    totalLoaded: number;
    totalDelivered: number;
    remaining: number;
  }[];
}

