    @apply font-sans antialiased bg-background text-foreground;
  }

  /* Ensure proper input visibility */
  input, textarea, select {
    @apply bg-white dark:bg-gray-800 border-2 border-gray-300 dark:border-gray-600 text-gray-900 dark:text-gray-100;
    @apply focus:border-blue-500 focus:ring-2 focus:ring-blue-200 dark:focus:ring-blue-800;
  }

  /* Mobile optimization */
  @media (max-width: 768px) {
    .container {
      @apply px-2 sm:px-4;
    }

    /* Ensure modals fit mobile screens */
    .modal-content {
      @apply max-w-[95vw] max-h-[95vh] overflow-auto;
    }

    /* Make tables responsive */
    .table-container {
      @apply overflow-x-auto;
    }

    /* Adjust card padding for mobile */
    .card-mobile {
      @apply p-3 sm:p-6;
    }
  }

  /* Fix button visibility */
  button {
    @apply focus:outline-none focus:ring-2 focus:ring-offset-2;
  }
}

