import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogTitle, DialogDescription, DialogHeader } from "@/components/ui/dialog";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Label } from "@/components/ui/label";
import { 
  FileTextIcon, 
  MessageCircleIcon, 
  UploadIcon, 
  ClockIcon, 
  UserIcon, 
  AlertCircleIcon,
  CheckCircleIcon,
  XCircleIcon,
  Loader2Icon,
  DownloadIcon,
  ImageIcon,
  FileIcon,
  EyeIcon,
  TrashIcon
} from "lucide-react";

interface TaskComment {
  id: string;
  text: string;
  author: string;
  timestamp: Date;
  type: 'comment' | 'status_change' | 'document_upload';
  oldStatus?: string;
  newStatus?: string;
}

interface TaskDocument {
  id: string;
  name: string;
  url: string;
  type: string;
  size: number;
  uploadedBy: string;
  uploadedAt: Date;
}

interface Task {
  id: string;
  title: string;
  description: string;
  priority: 'low' | 'medium' | 'high';
  status: 'pending' | 'in-progress' | 'completed';
  dueDate: Date;
  assignedTo?: string;
  createdAt: Date;
  updatedAt: Date;
  comments?: TaskComment[];
  documents?: TaskDocument[];
  attachments?: Array<{
    name: string;
    url: string;
    type: string;
    size: number;
  }>;
  lastUpdated?: Date;
  updatedBy?: string;
}

interface EnhancedTaskModalProps {
  task: Task | null;
  isOpen: boolean;
  onClose: () => void;
  onStatusUpdate: (taskId: string, newStatus: string) => Promise<void>;
  onAddComment: (taskId: string, comment: string) => Promise<void>;
  onUploadDocument: (taskId: string, files: FileList) => Promise<void>;
  onDeleteDocument?: (taskId: string, documentId: string) => Promise<void>;
  user: any;
  isUpdating?: boolean;
  isAddingComment?: boolean;
  isUploading?: boolean;
  onPhotoClick?: (url: string, label: string) => void;
}

function EnhancedTaskModal({
  task,
  isOpen,
  onClose,
  onStatusUpdate,
  onAddComment,
  onUploadDocument,
  onDeleteDocument,
  user,
  isUpdating = false,
  isAddingComment = false,
  isUploading = false,
  onPhotoClick
}: EnhancedTaskModalProps) {
  const [newComment, setNewComment] = useState('');
  const [selectedFiles, setSelectedFiles] = useState<FileList | null>(null);
  const [selectedStatus, setSelectedStatus] = useState<'pending' | 'in-progress' | 'completed'>(task?.status || 'pending');

  // Sync selectedStatus with task prop changes
  useEffect(() => {
    if (task?.status) {
      setSelectedStatus(task.status as 'pending' | 'in-progress' | 'completed');
      setNewComment('');
      setSelectedFiles(null);
    }
  }, [task?.id, task?.status]);

  if (!task) return null;

  const handleStatusChange = (newStatus: string) => {
    setSelectedStatus(newStatus as 'pending' | 'in-progress' | 'completed');
  };

  const handleSubmitStatusChange = async () => {
    if (selectedStatus !== task.status) {
      await onStatusUpdate(task.id, selectedStatus);
    }
  };

  const handleAddComment = async () => {
    if (!newComment.trim()) return;
    await onAddComment(task.id, newComment.trim());
    setNewComment('');
  };

  const handleFileUpload = async () => {
    if (!selectedFiles) return;
    await onUploadDocument(task.id, selectedFiles);
    setSelectedFiles(null);
  };

  const formatDate = (date: Date | string | any) => {
    if (!date) return 'N/A';

    let d: Date;
    if (typeof date === 'string') {
      d = new Date(date);
    } else if (date.toDate && typeof date.toDate === 'function') {
      // Handle Firebase Timestamp
      d = date.toDate();
    } else if (date instanceof Date) {
      d = date;
    } else {
      return 'Invalid Date';
    }

    if (!d || isNaN(d.getTime())) return 'Invalid Date';
    return d.toLocaleDateString() + ' ' + d.toLocaleTimeString([], {hour: '2-digit', minute: '2-digit'});
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return 'bg-red-100 text-red-800';
      case 'medium': return 'bg-yellow-100 text-yellow-800';
      case 'low': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed': return 'bg-green-100 text-green-800';
      case 'in-progress': return 'bg-blue-100 text-blue-800';
      case 'pending': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getCommentTypeIcon = (type: string) => {
    switch (type) {
      case 'comment': return <MessageCircleIcon className="h-4 w-4 text-blue-500" />;
      case 'status_change': return <AlertCircleIcon className="h-4 w-4 text-orange-500" />;
      case 'document_upload': return <FileTextIcon className="h-4 w-4 text-green-500" />;
      default: return <MessageCircleIcon className="h-4 w-4 text-gray-500" />;
    }
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const downloadFile = (url: string, filename: string) => {
    const link = document.createElement('a');
    link.href = url;
    link.download = filename;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const handleViewDocument = (doc: TaskDocument) => {
    if (onPhotoClick) {
      onPhotoClick(doc.url, doc.name);
    } else {
      window.open(doc.url, '_blank');
    }
  };

  const handleDownloadDocument = (doc: TaskDocument) => {
    downloadFile(doc.url, doc.name);
  };

  const handleDeleteDocument = async (doc: TaskDocument) => {
    if (!onDeleteDocument) return;

    try {
      await onDeleteDocument(task.id, doc.id);
    } catch (error) {
      console.error('Error deleting document:', error);
    }
  };

  const getFileIcon = (fileType: string) => {
    const type = fileType.toLowerCase();
    if (type.includes('image')) {
      return <ImageIcon className="h-4 w-4 text-green-500" />;
    } else if (type.includes('pdf')) {
      return <FileTextIcon className="h-4 w-4 text-red-500" />;
    } else {
      return <FileTextIcon className="h-4 w-4 text-blue-500" />;
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-7xl max-h-[95vh] w-[95vw] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <FileTextIcon className="h-5 w-5" />
            Task #{new Date(task.createdAt).getFullYear()}-{String(task.id).padStart(5, '0')}
          </DialogTitle>
          <DialogDescription>
            Task details, comments history, and document management
          </DialogDescription>
        </DialogHeader>

        <div className="grid grid-cols-1 xl:grid-cols-3 gap-6">
          {/* Main Task Details */}
          <div className="xl:col-span-2 space-y-4">
            {/* Task Info */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Task Information</CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="flex items-center gap-4">
                  <Badge className={getPriorityColor(task.priority)}>
                    {task.priority.toUpperCase()}
                  </Badge>
                  <Badge className={getStatusColor(task.status)}>
                    {task.status.replace('-', ' ').toUpperCase()}
                  </Badge>
                </div>

                <div>
                  <Label className="text-sm font-medium text-gray-700">Description</Label>
                  <p className="text-sm text-gray-600 mt-1">{task.description}</p>
                </div>

                <div className="grid grid-cols-2 gap-4 text-sm">
                  <div>
                    <Label className="text-gray-700">Assigned To</Label>
                    <p className="text-gray-600">{task.assignedTo || 'Unassigned'}</p>
                  </div>
                  <div>
                    <Label className="text-gray-700">Due Date</Label>
                    <p className="text-gray-600">{formatDate(task.dueDate)}</p>
                  </div>
                  <div>
                    <Label className="text-gray-700">Created</Label>
                    <p className="text-gray-600">{formatDate(task.createdAt)}</p>
                  </div>
                  <div>
                    <Label className="text-gray-700">Last Updated</Label>
                    <p className="text-gray-600">{task.lastUpdated ? formatDate(task.lastUpdated) : 'Never'}</p>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Comments History */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg flex items-center gap-2">
                  <MessageCircleIcon className="h-5 w-5" />
                  Comments & Activity History
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-3 max-h-60 overflow-y-auto">
                  {task.comments && task.comments.length > 0 ? (
                    task.comments
                      .sort((a, b) => new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime())
                      .map((comment) => (
                        <div key={comment.id} className="flex gap-3 p-3 bg-gray-50 rounded-lg">
                          {getCommentTypeIcon(comment.type)}
                          <div className="flex-1">
                            <div className="flex items-center gap-2 mb-1">
                              <span className="font-medium text-sm">{comment.author}</span>
                              <span className="text-xs text-gray-500">
                                {formatDate(comment.timestamp)}
                              </span>
                            </div>
                            <p className="text-sm text-gray-700">{comment.text}</p>
                            {comment.type === 'status_change' && (
                              <div className="mt-1">
                                <Badge className="text-xs bg-orange-100 text-orange-800">
                                  {comment.oldStatus} â†’ {comment.newStatus}
                                </Badge>
                              </div>
                            )}
                          </div>
                        </div>
                      ))
                  ) : (
                    <p className="text-gray-500 text-center py-4">No comments yet</p>
                  )}
                </div>

                {/* Add Comment */}
                <div className="mt-4 pt-4 border-t">
                  <div className="space-y-3">
                    <Textarea
                      value={newComment}
                      onChange={(e) => setNewComment(e.target.value)}
                      placeholder="Add a comment..."
                      rows={3}
                    />
                    <Button 
                      onClick={handleAddComment} 
                      disabled={!newComment.trim() || isAddingComment}
                      size="sm"
                    >
                      {isAddingComment ? (
                        <>
                          <Loader2Icon className="h-4 w-4 mr-2 animate-spin" />
                          Adding...
                        </>
                      ) : (
                        <>
                          <MessageCircleIcon className="h-4 w-4 mr-2" />
                          Add Comment
                        </>
                      )}
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Sidebar - Actions & Documents */}
          <div className="space-y-4">
            {/* Status Update */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Quick Actions</CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <div>
                  <Label className="text-sm font-medium mb-3 block">Update Status</Label>
                  <RadioGroup
                    value={selectedStatus}
                    onValueChange={handleStatusChange}
                    disabled={isUpdating}
                    className="space-y-2"
                  >
                    <div className="flex items-center space-x-3 p-3 border rounded-lg hover:bg-gray-50 cursor-pointer transition-colors">
                      <RadioGroupItem value="pending" id="status-pending" />
                      <Label htmlFor="status-pending" className="flex items-center gap-2 cursor-pointer flex-1">
                        <div className="w-3 h-3 bg-gray-500 rounded-full"></div>
                        <div>
                          <div className="font-medium">Pending</div>
                          <div className="text-xs text-gray-500">Not started yet</div>
                        </div>
                      </Label>
                    </div>

                    <div className="flex items-center space-x-3 p-3 border rounded-lg hover:bg-gray-50 cursor-pointer transition-colors">
                      <RadioGroupItem value="in-progress" id="status-in-progress" />
                      <Label htmlFor="status-in-progress" className="flex items-center gap-2 cursor-pointer flex-1">
                        <div className="w-3 h-3 bg-blue-500 rounded-full"></div>
                        <div>
                          <div className="font-medium">In Progress</div>
                          <div className="text-xs text-gray-500">Currently working</div>
                        </div>
                      </Label>
                    </div>

                    <div className="flex items-center space-x-3 p-3 border rounded-lg hover:bg-gray-50 cursor-pointer transition-colors">
                      <RadioGroupItem value="completed" id="status-completed" />
                      <Label htmlFor="status-completed" className="flex items-center gap-2 cursor-pointer flex-1">
                        <div className="w-3 h-3 bg-green-500 rounded-full"></div>
                        <div>
                          <div className="font-medium">Completed</div>
                          <div className="text-xs text-gray-500">Task finished</div>
                        </div>
                      </Label>
                    </div>
                  </RadioGroup>
                  {selectedStatus !== task.status && (
                    <div className="mt-3">
                      <Button 
                        onClick={handleSubmitStatusChange}
                        disabled={isUpdating}
                        size="sm"
                        className="w-full"
                      >
                        {isUpdating ? (
                          <>
                            <Loader2Icon className="h-4 w-4 mr-2 animate-spin" />
                            Updating...
                          </>
                        ) : (
                          'Update Status'
                        )}
                      </Button>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Task Attachments */}
            {task.attachments && task.attachments.length > 0 && (
              <Card>
                <CardHeader>
                  <CardTitle className="text-lg flex items-center gap-2">
                    <FileIcon className="h-5 w-5" />
                    Task Attachments ({task.attachments.length})
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-2 max-h-40 overflow-y-auto">
                    {task.attachments.map((attachment, index) => (
                      <div key={index} className="flex items-center justify-between p-2 bg-gray-50 rounded border">
                        <div className="flex items-center gap-2">
                          {attachment.type.startsWith('image/') ? (
                            <ImageIcon className="h-4 w-4 text-green-600" />
                          ) : (
                            <FileIcon className="h-4 w-4 text-blue-600" />
                          )}
                          <div className="flex-1 min-w-0">
                            <p className="text-sm font-medium truncate">{attachment.name}</p>
                            <p className="text-xs text-gray-500">{formatFileSize(attachment.size)}</p>
                          </div>
                        </div>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => downloadFile(attachment.url, attachment.name)}
                          className="text-blue-500 hover:text-blue-700"
                        >
                          <DownloadIcon className="h-4 w-4" />
                        </Button>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            )}

            {/* Documents */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg flex items-center gap-2">
                  <FileTextIcon className="h-5 w-5" />
                  Additional Documents ({task.documents?.length || 0})
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                {task.documents && task.documents.length > 0 ? (
                  <div className="space-y-2 max-h-64 overflow-y-auto">
                    {task.documents.map((doc) => (
                      <div key={doc.id} className="flex items-start gap-3 p-3 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors border">
                        <div className="mt-1">
                          {getFileIcon(doc.type)}
                        </div>
                        <div className="flex-1 min-w-0 space-y-1">
                          <p className="text-sm font-medium break-words" title={doc.name}>{doc.name}</p>
                          <div className="flex flex-wrap items-center gap-2 text-xs text-gray-500">
                            <span>{doc.uploadedBy}</span>
                            <span>â€¢</span>
                            <span>{formatDate(doc.uploadedAt)}</span>
                          </div>
                          <div className="flex items-center gap-2 text-xs text-gray-400">
                            <span>{formatFileSize(doc.size)}</span>
                            <span>â€¢</span>
                            <span className="break-all">{doc.type}</span>
                          </div>
                        </div>
                        <div className="flex items-center gap-1 flex-shrink-0">
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => handleViewDocument(doc)}
                            className="h-8 w-8 p-0"
                            title={doc.type.includes('image') || doc.type.includes('pdf') ? 'View document' : 'Open document'}
                            data-testid={`button-view-document-${doc.id}`}
                          >
                            <EyeIcon className="h-3 w-3" />
                          </Button>
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => handleDownloadDocument(doc)}
                            className="h-8 w-8 p-0"
                            title="Download document"
                            data-testid={`button-download-document-${doc.id}`}
                          >
                            <DownloadIcon className="h-3 w-3" />
                          </Button>
                          {onDeleteDocument && (
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => handleDeleteDocument(doc)}
                              className="h-8 w-8 p-0 text-red-600 hover:text-red-700 hover:bg-red-50"
                              title="Delete document"
                              data-testid={`button-delete-document-${doc.id}`}
                            >
                              <TrashIcon className="h-3 w-3" />
                            </Button>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <p className="text-gray-500 text-sm">No additional documents uploaded</p>
                )}

                {/* Upload Documents */}
                <div className="pt-3 border-t">
                  <Label className="text-sm font-medium">Upload Additional Documents</Label>
                  <Input
                    type="file"
                    multiple
                    onChange={(e) => setSelectedFiles(e.target.files)}
                    className="mt-1"
                    accept=".pdf,.doc,.docx,.txt,.jpg,.jpeg,.png"
                  />
                  {selectedFiles && selectedFiles.length > 0 && (
                    <div className="mt-2">
                      <p className="text-xs text-gray-600">
                        {selectedFiles.length} file(s) selected
                      </p>
                      <Button 
                        onClick={handleFileUpload}
                        disabled={isUploading}
                        size="sm"
                        className="mt-2 w-full"
                      >
                        {isUploading ? (
                          <>
                            <Loader2Icon className="h-4 w-4 mr-2 animate-spin" />
                            Uploading...
                          </>
                        ) : (
                          <>
                            <UploadIcon className="h-4 w-4 mr-2" />
                            Upload Files
                          </>
                        )}
                      </Button>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

export default EnhancedTaskModal;

client/src/components/LoadSessionManager.content:
import { useEffect, useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { 
  TruckIcon, 
  DropletIcon, 
  CheckCircleIcon
} from 'lucide-react';
import { getActiveLoadSessions } from '@/lib/firebase';

interface LoadSession {
  id: string;
  loadSessionId: string;
  oilTypeName: string;
  oilTypeId: string;
  totalLoadedLiters: number;
  remainingLiters: number;
  status: string;
  createdAt: any;
  loadCount: number;
}

interface LoadSessionManagerProps {
  onSelectSession?: (sessionId: string) => void;
  selectedSessionId?: string;
  className?: string;
}

export function LoadSessionManager({ onSelectSession, selectedSessionId, className }: LoadSessionManagerProps) {
  const [loadSessions, setLoadSessions] = useState<LoadSession[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    loadActiveSessions();
  }, []);

  const loadActiveSessions = async () => {
    try {
      const sessions = await getActiveLoadSessions();
      setLoadSessions(sessions as LoadSession[]);
    } catch (error) {
      console.error('Error loading active sessions:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const getProgressPercentage = (session: LoadSession) => {
    return Math.max(0, (session.remainingLiters / session.totalLoadedLiters) * 100);
  };

  if (isLoading) {
    return (
      <Card className={className}>
        <CardContent className="p-6">
          <div className="animate-pulse">
            <div className="h-4 bg-gray-200 rounded w-1/2 mb-2"></div>
            <div className="h-8 bg-gray-200 rounded mb-4"></div>
            <div className="h-2 bg-gray-200 rounded"></div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className={className}>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <TruckIcon className="w-5 h-5 text-blue-500" />
          Active Load Sessions
        </CardTitle>
      </CardHeader>
      <CardContent>
        {/* Filter out completed sessions (balance = 0) */}
        {loadSessions.filter((session: any) => session.remainingLiters > 0).length === 0 ? (
          <div className="text-center py-6">
            <DropletIcon className="w-12 h-12 text-gray-300 mx-auto mb-2" />
            <p className="text-gray-500 mb-2">No active load sessions</p>
            <p className="text-sm text-gray-400">Start loading oil to create a new session</p>
          </div>
        ) : (
          <div className="space-y-4">
            {loadSessions.filter((session: any) => session.remainingLiters > 0).map((session) => (
              <div 
                key={session.id}
                className={`p-4 border rounded-lg cursor-pointer transition-all ${
                  selectedSessionId === session.loadSessionId 
                    ? 'border-blue-500 bg-blue-50' 
                    : 'border-gray-200 hover:border-gray-300'
                }`}
                onClick={() => onSelectSession?.(session.loadSessionId)}
              >
                <div className="flex items-center justify-between mb-3">
                  <div>
                    <div className="flex items-center gap-2">
                      <h4 className="font-medium text-gray-900">{session.loadSessionId}</h4>
                      <Badge variant="outline">{session.oilTypeName}</Badge>
                    </div>
                    <p className="text-sm text-gray-500">
                      {session.loadCount > 1 && `${session.loadCount} loads â€¢ `}
                      {new Date(session.createdAt.toDate?.() || session.createdAt).toLocaleDateString()}
                    </p>
                  </div>
                  <div className="text-right">
                    <div className={`text-lg font-bold ${session.remainingLiters < 0 ? 'text-red-600' : 'text-gray-900'}`}>
                      {session.remainingLiters.toLocaleString()}L
                    </div>
                    <div className="text-sm text-gray-500">
                      of {session.totalLoadedLiters.toLocaleString()}L
                    </div>
                  </div>
                </div>

                <div className="space-y-2">
                  <div className="flex justify-between text-sm">
                    <span className="text-gray-600">Balance</span>
                    <span className={`font-medium ${session.remainingLiters < 0 ? 'text-red-600' : 'text-gray-900'}`}>
                      {session.remainingLiters < 0 ? 'Negative' : `${Math.round(getProgressPercentage(session))}%`}
                    </span>
                  </div>
                  <Progress 
                    value={session.remainingLiters < 0 ? 0 : getProgressPercentage(session)} 
                    className="h-2"
                  />
                  {session.remainingLiters < 0 && (
                    <div className="text-xs text-red-600 font-medium">
                      Overdrawn by {Math.abs(session.remainingLiters).toLocaleString()}L
                    </div>
                  )}
                </div>

                {session.remainingLiters === 0 && (
                  <div className="mt-3 flex items-center justify-center gap-2 text-green-600">
                    <CheckCircleIcon className="w-4 h-4" />
                    <span className="text-sm font-medium">Session Completed</span>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/LoadingWorkflow.ts.content:
import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { PhotoCaptureButton } from './PhotoCaptureButton';
import { 
  TruckIcon, 
  CameraIcon, 
  CheckIcon, 
  DropletIcon,
  GaugeIcon,
  PlusIcon,
  ImageIcon
} from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { useQuery } from '@tanstack/react-query';

import { createLoadSession, getOilTypes, getActiveBranchesOnly, uploadPhotoToFirebaseStorage } from '@/lib/firebase';
import { safeWatermarkImage } from '@/utils/watermark';
import { useAuth } from '@/hooks/useAuth';

interface LoadingData {
  deliveryOrderNo: string;
  oilTypeId: string;
  totalLoadedLiters: number;
  loadMeterReading: number;
  loadLocationId?: string;
  meterReadingPhoto?: string;
}

interface LoadingStep {
  id: number;
  title: string;
  status: 'pending' | 'active' | 'completed';
}

interface LoadingWorkflowProps {
  onClose: () => void;
  onPhotoClick?: (url: string, label: string) => void;
}

export function LoadingWorkflow({ onClose, onPhotoClick }: LoadingWorkflowProps) {
  const { toast } = useToast();
  const { userData: user } = useAuth();
  const [currentStep, setCurrentStep] = useState(1);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [loadingData, setLoadingData] = useState<LoadingData>({
    deliveryOrderNo: '',
    oilTypeId: '',
    totalLoadedLiters: 0,
    loadMeterReading: 0,
    loadLocationId: '',
  });

  // Fetch oil types and branches from Firestore
  const { data: oilTypes = [] } = useQuery({ 
    queryKey: ['oil-types'], 
    queryFn: getOilTypes 
  });
  const { data: branches = [] } = useQuery({ 
    queryKey: ['branches'], 
    queryFn: getActiveBranchesOnly 
  });

  const steps: LoadingStep[] = [
    { id: 1, title: 'Complete Tank Loading', status: currentStep === 1 ? 'active' : currentStep > 1 ? 'completed' : 'pending' },
  ];

  const handlePhotoCapture = async (photoBlob: Blob, photoType: string) => {
    try {
      // Convert blob to file for watermarking
      const originalFile = new File([photoBlob], `${photoType}_${Date.now()}.jpg`, {
        type: 'image/jpeg',
        lastModified: Date.now()
      });

      // Get branch name for watermarking
      const selectedBranch = branches.find(b => b.id === loadingData.loadLocationId);
      const branchName = selectedBranch?.name || 'Loading Location';

      // Apply watermark with loading-specific details
      const watermarkedFile = await safeWatermarkImage(originalFile, {
        branchName,
        timestamp: new Date(),
        extraLine2: "Oil Type: Loading"
      });

      // Upload watermarked image
      const watermarkedBlob = new Blob([watermarkedFile], { type: 'image/jpeg' });
      const photoUrl = await uploadPhotoToFirebaseStorage(watermarkedBlob, 'loading-photos');
      setLoadingData(prev => ({ ...prev, [`${photoType}Photo`]: photoUrl }));

      toast({
        title: "Photo Captured",
        description: `${photoType} photo saved successfully with watermark`
      });
    } catch (error) {
      console.error('Photo upload error:', error);
      toast({
        title: "Photo Saved Locally", 
        description: "Photo captured and saved for demo",
        variant: "default"
      });
    }
  };

  const handleCompleteLoading = async () => {
    if (isSubmitting) return; // Prevent double submission

    try {
      setIsSubmitting(true);

      // Validate required fields
      if (!loadingData.oilTypeId || !loadingData.totalLoadedLiters) {
        toast({
          title: "Missing Information",
          description: "Please fill in all required fields",
          variant: "destructive"
        });
        return;
      }

      // Get current user information
      const currentUser = user;
      const driverName = currentUser?.displayName || currentUser?.email || 'Unknown Driver';

      // Get selected branch/location information
      const selectedBranch = branches.find((b: any) => b.id === loadingData.loadLocationId);
      const loadLocationName = selectedBranch?.name || (loadingData.loadLocationId ? 'Unknown Location' : 'Main Depot');

      // Get selected oil type information
      const selectedOilType = (oilTypes as any[]).find((type: any) => type.id === loadingData.oilTypeId);
      const oilTypeName = selectedOilType?.name || 'Unknown Oil Type';

      // Create load session directly in Firestore with complete information
      const loadSessionData = {
        deliveryOrderNo: loadingData.deliveryOrderNo || '',
        oilTypeId: loadingData.oilTypeId,
        oilTypeName: oilTypeName,
        totalLoadedLiters: loadingData.totalLoadedLiters,
        loadMeterReading: loadingData.loadMeterReading,
        loadLocationId: loadingData.loadLocationId || 'main-depot',
        loadLocationName: loadLocationName,
        meterReadingPhoto: loadingData.meterReadingPhoto || null,
        driverName: driverName,
        driverUid: currentUser?.uid || 'unknown'
      };

      const loadSession = await createLoadSession(loadSessionData);

      toast({
        title: "Load Session Created",
        description: `Successfully loaded ${loadingData.totalLoadedLiters}L of ${loadSessionData.oilTypeName}. Session ID: ${loadSession.loadSessionId}`
      });

      // Reset form
      setLoadingData({
        deliveryOrderNo: '',
        oilTypeId: '',
        totalLoadedLiters: 0,
        loadMeterReading: 0,
        loadLocationId: '',
      });
      setCurrentStep(1);
      onClose(); // Close and return to dashboard
    } catch (error) {
      console.error('Load session creation error:', error);
      toast({
        title: "Loading Failed",
        description: "Failed to create load session. Please try again.",
        variant: "destructive"
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const canCompleteLoading = () => {
    return loadingData.oilTypeId && loadingData.totalLoadedLiters > 0 && 
           loadingData.loadMeterReading > 0 && 
           loadingData.meterReadingPhoto;
  };

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      {/* Header */}
      <div className="text-center">
        <h1 className="text-3xl font-bold text-gray-900">Tank Loading Process</h1>
        <p className="text-gray-600 mt-2">Load oil into tank truck and create load session</p>
      </div>

      {/* Progress Steps */}
      <div className="flex items-center justify-center space-x-4 mb-8">
        {steps.map((step, index) => (
          <div key={step.id} className="flex items-center">
            <div className={`flex items-center justify-center w-10 h-10 rounded-full border-2 ${
              step.status === 'completed' 
                ? 'bg-green-500 border-green-500 text-white' 
                : step.status === 'active'
                ? 'bg-orange-500 border-orange-500 text-white'
                : 'bg-gray-200 border-gray-300 text-gray-600'
            }`}>
              {step.status === 'completed' ? (
                <CheckIcon className="w-6 h-6" />
              ) : (
                <span className="text-sm font-medium">{step.id}</span>
              )}
            </div>
            <span className={`ml-2 text-sm font-medium ${
              step.status === 'active' ? 'text-orange-600' : 'text-gray-600'
            }`}>
              {step.title}
            </span>
            {index < steps.length - 1 && (
              <div className={`w-12 h-0.5 mx-4 ${
                step.status === 'completed' ? 'bg-green-500' : 'bg-gray-300'
              }`} />
            )}
          </div>
        ))}
      </div>

      {/* Single Complete Loading Form */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <TruckIcon className="w-5 h-5 text-orange-500" />
            Complete Tank Loading
          </CardTitle>
        </CardHeader>

        <CardContent className="space-y-6">
          {/* Loading Details */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <Label htmlFor="deliveryOrder">Order/Delivery Number</Label>
              <Input
                data-testid="input-delivery-order"
                type="text"
                placeholder="Enter order/delivery number"
                value={loadingData.deliveryOrderNo}
                onChange={(e) => setLoadingData(prev => ({ ...prev, deliveryOrderNo: e.target.value }))}
              />
            </div>

            <div>
              <Label htmlFor="oilType">Oil Type *</Label>
              <Select 
                value={loadingData.oilTypeId} 
                onValueChange={(value) => setLoadingData(prev => ({ ...prev, oilTypeId: value }))}
              >
                <SelectTrigger data-testid="select-oil-type">
                  <SelectValue placeholder="Select oil type" />
                </SelectTrigger>
                <SelectContent>
                  {oilTypes.map((type: any) => (
                    <SelectItem key={type.id} value={type.id} data-testid={`oil-type-${type.id}`}>
                      {type.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="loadLocation">Load Location (Optional)</Label>
              <Select 
                value={loadingData.loadLocationId} 
                onValueChange={(value) => setLoadingData(prev => ({ ...prev, loadLocationId: value }))}
              >
                <SelectTrigger data-testid="select-load-location">
                  <SelectValue placeholder="Select location" />
                </SelectTrigger>
                <SelectContent>
                  {branches.map((branch: any) => (
                    <SelectItem key={branch.id} value={branch.id} data-testid={`location-${branch.id}`}>
                      {branch.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="totalLiters">Total Loaded (Liters) *</Label>
              <Input
                data-testid="input-total-liters"
                type="number"
                placeholder="Enter total loaded liters"
                value={loadingData.totalLoadedLiters === 0 ? '' : loadingData.totalLoadedLiters}
                onChange={(e) => setLoadingData(prev => ({ ...prev, totalLoadedLiters: Number(e.target.value) || 0 }))}
              />
            </div>

            <div>
              <Label htmlFor="meterReading">Load Meter Reading *</Label>
              <Input
                data-testid="input-meter-reading"
                type="number"
                placeholder="Enter meter reading"
                value={loadingData.loadMeterReading === 0 ? '' : loadingData.loadMeterReading}
                onChange={(e) => setLoadingData(prev => ({ ...prev, loadMeterReading: Number(e.target.value) || 0 }))}
              />
            </div>
          </div>

          {/* Photo Capture Section */}
          <div className="border-2 border-dashed border-gray-300 rounded-lg p-6">
            <div className="text-center">
              <CameraIcon className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">Main Tanker Meter Reading Photo *</h3>
              <p className="text-gray-600 mb-4">Capture the meter reading during loading</p>

              <PhotoCaptureButton
                onCapture={(blob, timestamp) => handlePhotoCapture(blob, 'meterReading')}
                className="bg-orange-500 hover:bg-orange-600 text-white px-6 py-2"
                title="Main Tanker Meter Reading Photo"
                branchName={branches.find((b: any) => b.id === loadingData.loadLocationId)?.name || 'Loading Location'}
              >
                <GaugeIcon className="w-4 h-4 mr-2" />
                Take Meter Reading Photo
              </PhotoCaptureButton>

              {loadingData.meterReadingPhoto && (
                <div className="mt-4 flex flex-col items-center">
                  <div className="relative group cursor-pointer mb-2"
                       onClick={() => onPhotoClick && onPhotoClick(loadingData.meterReadingPhoto!, 'Meter Reading Photo')}>
                    <img 
                      src={loadingData.meterReadingPhoto} 
                      alt="Meter Reading" 
                      className="w-20 h-20 object-cover rounded-lg border hover:opacity-90 transition-opacity"
                    />
                    <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-30 transition-all rounded-lg flex items-center justify-center">
                      <ImageIcon className="h-5 w-5 text-white opacity-0 group-hover:opacity-100 transition-opacity" />
                    </div>
                  </div>
                  <Badge variant="default" className="bg-green-100 text-green-800">
                    <CheckIcon className="w-4 h-4 mr-1" />
                    Photo Captured
                  </Badge>
                </div>
              )}
            </div>
          </div>

          {/* Submit Button */}
          <div className="flex justify-end">
            <Button 
              onClick={handleCompleteLoading}
              disabled={!canCompleteLoading() || isSubmitting}
              className="bg-green-600 hover:bg-green-700 px-8"
              data-testid="button-create-load-session"
            >
              {isSubmitting ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Submitting...
                </>
              ) : (
                <>
                  <PlusIcon className="w-4 h-4 mr-2" />
                  Loading Submit
                </>
              )}
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/PhotoCaptureButton.content:
import { ReactNode, useState } from 'react';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogTitle, DialogDescription, DialogHeader } from '@/components/ui/dialog';
import { CameraCapture } from './CameraCapture';

interface PhotoCaptureButtonProps {
  onCapture: (blob: Blob, timestamp: string) => void;
  className?: string;
  title: string;
  branchName: string;
  children: ReactNode;
}

export function PhotoCaptureButton({
  onCapture,
  className,
  title,
  branchName,
  children
}: PhotoCaptureButtonProps) {
  const [showCamera, setShowCamera] = useState(false);

  const handleCapture = (blob: Blob, timestamp: string) => {
    onCapture(blob, timestamp);
    setShowCamera(false);
  };

  return (
    <>
      <Button 
        onClick={() => setShowCamera(true)} 
        className={className}
        data-testid="button-photo-capture"
      >
        {children}
      </Button>

      <Dialog open={showCamera} onOpenChange={setShowCamera}>
        <DialogContent className="max-w-2xl" aria-describedby="camera-dialog-description">
          <DialogHeader>
            <DialogTitle>{title}</DialogTitle>
            <DialogDescription id="camera-dialog-description">
              Take a photo as evidence for {branchName}
            </DialogDescription>
          </DialogHeader>
          <CameraCapture
            isOpen={showCamera}
            onCapture={handleCapture}
            onClose={() => setShowCamera(false)}
            title={title}
            branchName={branchName}
          />
        </DialogContent>
      </Dialog>
    </>
  );
}

