export const deleteDriver = async (id: string) => {
  try {
    console.log('ðŸ—‘ï¸ Starting complete user deletion for:', id);

    // Step 1: Get user data before deletion
    const userRef = doc(db, 'users', id);
    const userSnap = await getDoc(userRef);

    if (!userSnap.exists()) {
      throw new Error('User not found in database');
    }

    const userData = userSnap.data();
    console.log('ðŸ“‹ User data retrieved:', userData.email);

    // Step 2: Delete related data first (cascading delete)
    const batch = writeBatch(db);

    // Delete user's complaints
    const complaintsQuery = query(
      collection(db, 'complaints'),
      where('reportedBy', '==', id)
    );
    const complaintsSnapshot = await getDocs(complaintsQuery);
    complaintsSnapshot.docs.forEach((doc) => {
      batch.delete(doc.ref);
    });
    console.log(`ðŸ“ Queued ${complaintsSnapshot.docs.length} complaints for deletion`);

    // Delete user's tasks (if assigned)
    const tasksQuery = query(
      collection(db, 'tasks'),
      where('assignedTo', '==', id)
    );
    const tasksSnapshot = await getDocs(tasksQuery);
    tasksSnapshot.docs.forEach((doc) => {
      batch.delete(doc.ref);
    });
    console.log(`ðŸ“‹ Queued ${tasksSnapshot.docs.length} tasks for deletion`);

    // Delete user's transactions/deliveries
    const transactionsQuery = query(
      collection(db, 'transactions'),
      where('driverId', '==', id)
    );
    const transactionsSnapshot = await getDocs(transactionsQuery);
    transactionsSnapshot.docs.forEach((doc) => {
      batch.delete(doc.ref);
    });
    console.log(`ðŸš› Queued ${transactionsSnapshot.docs.length} transactions for deletion`);

    // Delete user from Firestore
    batch.delete(userRef);

    // Step 3: Execute batch delete for Firestore data
    await batch.commit();
    console.log('âœ… Firestore data deleted successfully');

    // Step 4: Delete from Firebase Authentication
    try {
      console.log('ðŸ”„ Attempting Firebase Auth deletion via server...');
      const response = await fetch('/api/admin/delete-user', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ uid: id, email: userData.email })
      });

      if (response.ok) {
        const result = await response.json();
        if (result.authDeleted) {
          console.log('âœ… Firebase Auth user deleted successfully');
        } else {
          console.warn('âš ï¸ Firebase Auth deletion not configured:', result.manualStep);
        }
      } else {
        console.warn('âš ï¸ Server responded with error for Auth deletion');
      }
    } catch (authError) {
      console.warn('âš ï¸ Firebase Auth deletion request failed:', authError);
    }

    console.log('ðŸŽ‰ User deletion completed:', userData.email);

  } catch (error) {
    console.error('Error deleting driver:', error);
    throw error;
  }
};

