import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { 
  TruckIcon, 
  UsersIcon, 
  MapPinIcon, 
  DropletIcon,
  ClockIcon,
  AlertTriangleIcon,
  CheckCircleIcon,
  XCircleIcon
} from "lucide-react";

interface User {
  id: string;
  displayName: string;
  email: string;
  role: string;
  active: boolean;
}

interface Branch {
  id: string;
  name: string;
  address: string;
  contactNo: string;
}

interface OilType {
  id: string;
  name: string;
  color: string;
}

interface Task {
  id: string;
  title: string;
  status: 'pending' | 'in-progress' | 'completed';
  priority: 'low' | 'medium' | 'high';
  dueDate: Date;
}

interface AdminHomeProps {
  user: User;
  drivers: User[];
  branches: Branch[];
  oilTypes: OilType[];
  tasks: Task[];
  recentDeliveries: any[];
}

export default function AdminHome({ 
  user, 
  drivers, 
  branches, 
  oilTypes, 
  tasks, 
  recentDeliveries 
}: AdminHomeProps) {
  const activeDrivers = drivers.filter(d => d.active);
  const pendingTasks = tasks.filter(t => t.status === 'pending');
  const highPriorityTasks = tasks.filter(t => t.priority === 'high' && t.status !== 'completed');

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'in-progress': return 'bg-blue-100 text-blue-800';
      case 'completed': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high': return 'bg-red-100 text-red-800';
      case 'medium': return 'bg-yellow-100 text-yellow-800';
      case 'low': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="space-y-6">
      {/* Welcome Header */}
      <div className="bg-gradient-to-r from-blue-600 to-purple-600 rounded-lg p-6 text-white">
        <h1 className="text-2xl font-bold mb-2">Welcome back, {user.displayName}!</h1>
        <p className="text-blue-100">Here's an overview of your oil delivery operations</p>
      </div>

      {/* Quick Stats */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Active Drivers</CardTitle>
            <UsersIcon className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{activeDrivers.length}</div>
            <p className="text-xs text-muted-foreground">
              of {drivers.length} total drivers
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Branches</CardTitle>
            <MapPinIcon className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{branches.length}</div>
            <p className="text-xs text-muted-foreground">
              locations managed
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Oil Types</CardTitle>
            <DropletIcon className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{oilTypes.length}</div>
            <p className="text-xs text-muted-foreground">
              products available
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Pending Tasks</CardTitle>
            <ClockIcon className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{pendingTasks.length}</div>
            <p className="text-xs text-muted-foreground">
              tasks awaiting action
            </p>
          </CardContent>
        </Card>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* High Priority Tasks */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <AlertTriangleIcon className="h-5 w-5 text-red-500" />
              High Priority Tasks
            </CardTitle>
          </CardHeader>
          <CardContent>
            {highPriorityTasks.length > 0 ? (
              <div className="space-y-3">
                {highPriorityTasks.slice(0, 5).map((task) => (
                  <div key={task.id} className="flex items-center justify-between p-3 bg-red-50 rounded-lg">
                    <div>
                      <p className="font-medium text-sm">{task.title}</p>
                      <p className="text-xs text-gray-600">
                        Due: {task.dueDate.toLocaleDateString()}
                      </p>
                    </div>
                    <div className="flex space-x-2">
                      <Badge className={getPriorityColor(task.priority)}>
                        {task.priority}
                      </Badge>
                      <Badge className={getStatusColor(task.status)}>
                        {task.status}
                      </Badge>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center py-6 text-gray-500">
                <CheckCircleIcon className="h-8 w-8 mx-auto mb-2 text-green-500" />
                <p>No high priority tasks</p>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Recent Activity */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <TruckIcon className="h-5 w-5 text-blue-500" />
              Recent Deliveries
            </CardTitle>
          </CardHeader>
          <CardContent>
            {recentDeliveries.length > 0 ? (
              <div className="space-y-3">
                {recentDeliveries.slice(0, 5).map((delivery, index) => (
                  <div key={index} className="flex items-center justify-between p-3 bg-blue-50 rounded-lg">
                    <div>
                      <p className="font-medium text-sm">
                        {delivery.customerName || 'Customer'}
                      </p>
                      <p className="text-xs text-gray-600">
                        {delivery.oilType} - {delivery.quantity}L
                      </p>
                    </div>
                    <Badge className={delivery.status === 'completed' ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'}>
                      {delivery.status}
                    </Badge>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-center py-6 text-gray-500">
                <XCircleIcon className="h-8 w-8 mx-auto mb-2" />
                <p>No recent deliveries</p>
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Quick Actions */}
      <Card>
        <CardHeader>
          <CardTitle>Quick Actions</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="p-4 border border-gray-200 rounded-lg text-center hover:bg-gray-50 cursor-pointer">
              <UsersIcon className="h-8 w-8 mx-auto mb-2 text-blue-500" />
              <p className="text-sm font-medium">Add Driver</p>
            </div>
            <div className="p-4 border border-gray-200 rounded-lg text-center hover:bg-gray-50 cursor-pointer">
              <MapPinIcon className="h-8 w-8 mx-auto mb-2 text-green-500" />
              <p className="text-sm font-medium">Add Branch</p>
            </div>
            <div className="p-4 border border-gray-200 rounded-lg text-center hover:bg-gray-50 cursor-pointer">
              <DropletIcon className="h-8 w-8 mx-auto mb-2 text-orange-500" />
              <p className="text-sm font-medium">Add Oil Type</p>
            </div>
            <div className="p-4 border border-gray-200 rounded-lg text-center hover:bg-gray-50 cursor-pointer">
              <ClockIcon className="h-8 w-8 mx-auto mb-2 text-purple-500" />
              <p className="text-sm font-medium">Create Task</p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/admin-oil-types.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { PlusIcon, EditIcon, TrashIcon, DropletIcon } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface OilType {
  id: string;
  name: string;
  color: string;
}

interface InsertOilType {
  name: string;
  color: string;
}

interface AdminOilTypesProps {
  oilTypes: OilType[];
  onAddOilType: (oilType: InsertOilType) => void;
  onUpdateOilType: (id: string, oilType: Partial<OilType>) => void;
  onDeleteOilType: (id: string) => void;
}

const colorOptions = [
  { name: 'Red', value: '#ef4444' },
  { name: 'Orange', value: '#f97316' },
  { name: 'Yellow', value: '#eab308' },
  { name: 'Green', value: '#22c55e' },
  { name: 'Blue', value: '#3b82f6' },
  { name: 'Purple', value: '#a855f7' },
  { name: 'Pink', value: '#ec4899' },
  { name: 'Gray', value: '#6b7280' }
];

export default function AdminOilTypes({ 
  oilTypes, 
  onAddOilType, 
  onUpdateOilType, 
  onDeleteOilType
}: AdminOilTypesProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [editingOilType, setEditingOilType] = useState<OilType | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    color: '#3b82f6'
  });
  const { toast } = useToast();

  const resetForm = () => {
    setFormData({
      name: '',
      color: '#3b82f6'
    });
    setEditingOilType(null);
  };

  const handleSubmit = async () => {
    if (isSubmitting) return; // Prevent double submission

    try {
      setIsSubmitting(true);

      if (!formData.name) {
        toast({
          title: "Missing Information",
          description: "Please enter an oil type name",
          variant: "destructive"
        });
        return;
      }

      if (editingOilType) {
        onUpdateOilType(editingOilType.id, formData);
        toast({
          title: "Success",
          description: "Oil type updated successfully"
        });
      } else {
        onAddOilType(formData);
        toast({
          title: "Success",
          description: "Oil type added successfully"
        });
      }

      resetForm();
      setIsOpen(false);
    } catch (error) {
      console.error('Error submitting oil type:', error);
      toast({
        title: "Error",
        description: "Failed to save oil type",
        variant: "destructive"
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleEdit = (oilType: OilType) => {
    setEditingOilType(oilType);
    setFormData({
      name: oilType.name,
      color: oilType.color
    });
    setIsOpen(true);
  };

  const handleDelete = (id: string) => {
    if (window.confirm('Are you sure you want to delete this oil type?')) {
      onDeleteOilType(id);
      toast({
        title: "Success",
        description: "Oil type deleted successfully"
      });
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-bold">Oil Type Management</h2>
          <p className="text-gray-600">Manage the types of oil products you deliver</p>
        </div>
        <Dialog open={isOpen} onOpenChange={setIsOpen}>
          <DialogTrigger asChild>
            <Button onClick={() => { resetForm(); setIsOpen(true); }}>
              <PlusIcon className="h-4 w-4 mr-2" />
              Add Oil Type
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>{editingOilType ? 'Edit Oil Type' : 'Add New Oil Type'}</DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <div>
                <Label htmlFor="name">Oil Type Name</Label>
                <Input
                  id="name"
                  value={formData.name}
                  onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                  placeholder="e.g., Diesel, Petrol, Motor Oil"
                />
              </div>

              <div>
                <Label>Color</Label>
                <div className="grid grid-cols-4 gap-3 mt-2">
                  {colorOptions.map((color) => (
                    <button
                      key={color.value}
                      type="button"
                      className={`
                        flex items-center justify-center p-3 rounded-lg border-2 transition-all
                        ${formData.color === color.value 
                          ? 'border-gray-800 scale-105' 
                          : 'border-gray-200 hover:border-gray-400'
                        }
                      `}
                      onClick={() => setFormData(prev => ({ ...prev, color: color.value }))}
                    >
                      <div className="flex flex-col items-center space-y-1">
                        <div 
                          className="w-6 h-6 rounded-full"
                          style={{ backgroundColor: color.value }}
                        />
                        <span className="text-xs text-gray-600">{color.name}</span>
                      </div>
                    </button>
                  ))}
                </div>
              </div>

              <div className="p-4 bg-gray-50 rounded-lg">
                <Label className="text-sm font-medium mb-2 block">Preview</Label>
                <div className="flex items-center space-x-3">
                  <div 
                    className="w-8 h-8 rounded-full flex items-center justify-center"
                    style={{ backgroundColor: formData.color }}
                  >
                    <DropletIcon className="h-4 w-4 text-white" />
                  </div>
                  <span className="font-medium">
                    {formData.name || 'Oil Type Name'}
                  </span>
                </div>
              </div>

              <div className="flex justify-end space-x-2">
                <Button variant="outline" onClick={() => setIsOpen(false)}>
                  Cancel
                </Button>
                <Button 
                  onClick={handleSubmit}
                  disabled={isSubmitting}
                >
                  {isSubmitting ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                      {editingOilType ? 'Updating...' : 'Adding...'}
                    </>
                  ) : (
                    `${editingOilType ? 'Update' : 'Add'} Oil Type`
                  )}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
        {oilTypes?.map((oilType) => (
          <Card key={oilType.id} className="hover:shadow-lg transition-shadow">
            <CardHeader>
              <div className="flex justify-between items-start">
                <div className="flex items-center space-x-3">
                  <div 
                    className="w-10 h-10 rounded-full flex items-center justify-center"
                    style={{ backgroundColor: oilType.color }}
                  >
                    <DropletIcon className="h-5 w-5 text-white" />
                  </div>
                  <div>
                    <CardTitle className="text-lg">{oilType.name}</CardTitle>
                    <p className="text-sm text-gray-600" style={{ color: oilType.color }}>
                      {oilType.color}
                    </p>
                  </div>
                </div>
                <div className="flex space-x-1">
                  <Button variant="outline" size="sm" onClick={() => handleEdit(oilType)}>
                    <EditIcon className="h-4 w-4" />
                  </Button>
                  <Button variant="destructive" size="sm" onClick={() => handleDelete(oilType.id)}>
                    <TrashIcon className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-600">Color Code:</span>
                  <div className="flex items-center space-x-2">
                    <div 
                      className="w-4 h-4 rounded border"
                      style={{ backgroundColor: oilType.color }}
                    />
                    <code className="text-xs bg-gray-100 px-2 py-1 rounded">
                      {oilType.color}
                    </code>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {(!oilTypes || oilTypes.length === 0) && (
        <Card className="text-center py-12">
          <CardContent>
            <DropletIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">No oil types yet</h3>
            <p className="text-gray-600 mb-4">Get started by adding your first oil type.</p>
            <Button onClick={() => setIsOpen(true)}>
              <PlusIcon className="h-4 w-4 mr-2" />
              Add Your First Oil Type
            </Button>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

