                            {complaint.status?.replace('-', ' ').replace('_', ' ') || 'open'}
                          </Badge>
                          <Badge variant="outline" className="capitalize">
                            Priority: {complaint.priority || 'medium'}
                          </Badge>
                          {complaint.resolvedAt && (
                            <Badge variant="secondary" className="text-xs">
                              Resolved
                            </Badge>
                          )}
                        </div>
                      </div>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-3">
                        <div>
                          <p className="text-sm text-gray-600 font-medium">Description:</p>
                          <p className="text-sm mt-1">{complaint.description || 'No description provided'}</p>
                        </div>

                        <div className="grid grid-cols-2 gap-4">
                          <div>
                            <p className="text-sm text-gray-600 font-medium">Branch:</p>
                            <p className="text-sm">{complaint.branchName || 'Not specified'}</p>
                          </div>
                          <div>
                            <p className="text-sm text-gray-600 font-medium">Priority:</p>
                            <p className="text-sm capitalize">{complaint.priority || 'medium'}</p>
                          </div>
                        </div>

                        {/* Admin Actions & Resolution */}
                        {(complaint.adminNotes || complaint.resolutionNotes || complaint.resolvedAt) && (
                          <div className="border-t pt-3">
                            <p className="text-sm text-gray-600 font-medium mb-2">Admin Actions:</p>
                            <div className="space-y-2">
                              {complaint.adminNotes && (
                                <div className="bg-blue-50 p-2 rounded border-l-2 border-blue-400">
                                  <p className="text-xs text-blue-600 font-medium">Admin Notes:</p>
                                  <p className="text-sm">{complaint.adminNotes}</p>
                                </div>
                              )}
                              {complaint.resolutionNotes && (
                                <div className="bg-green-50 p-2 rounded border-l-2 border-green-400">
                                  <p className="text-xs text-green-600 font-medium">Resolution:</p>
                                  <p className="text-sm">{complaint.resolutionNotes}</p>
                                </div>
                              )}
                              {complaint.resolvedAt && (
                                <div className="text-xs text-gray-500">
                                  Resolved on: {formatComplaintDate(complaint.resolvedAt)}
                                </div>
                              )}
                            </div>
                          </div>
                        )}

                        {/* Status Timeline */}
                        <div className="border-t pt-3">
                          <p className="text-sm text-gray-600 font-medium mb-2">Status History:</p>
                          <div className="space-y-1">
                            <div className="flex justify-between text-xs">
                              <span>Submitted:</span>
                              <span>{formatComplaintDate(complaint.createdAt, true)}</span>
                            </div>
                            {complaint.updatedAt && (
                              <div className="flex justify-between text-xs">
                                <span>Last Updated:</span>
                                <span>{formatComplaintDate(complaint.updatedAt, true)}</span>
                              </div>
                            )}
                            {complaint.resolvedAt && (
                              <div className="flex justify-between text-xs text-green-600">
                                <span>Resolved:</span>
                                <span>{formatComplaintDate(complaint.resolvedAt, true)}</span>
                              </div>
                            )}
                          </div>
                        </div>

                        {complaint.photos && complaint.photos.length > 0 && (
                          <div>
                            <p className="text-sm text-gray-600 font-medium mb-2">Photos ({complaint.photos.length}):</p>
                            <div className="grid grid-cols-4 gap-2">
                              {complaint.photos.map((photoUrl: string, photoIndex: number) => (
                                <div key={photoIndex} className="relative group cursor-pointer"
                                     onClick={() => {
                                       setSelectedPhoto({
                                         url: photoUrl,
                                         label: `Complaint Photo ${photoIndex + 1}`
                                       });
                                       setShowPhotoModal(true);
                                     }}>
                                  <img 
                                    src={photoUrl} 
                                    alt={`Complaint photo ${photoIndex + 1}`} 
                                    className="w-full h-16 object-cover rounded border hover:opacity-90 transition-opacity"
                                  />
                                  <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-30 transition-all rounded flex items-center justify-center">
                                    <EyeIcon className="h-4 w-4 text-white opacity-0 group-hover:opacity-100 transition-opacity" />
                                  </div>
                                </div>
                              ))}
                            </div>
                          </div>
                        )}

                        <div className="flex justify-between items-center pt-2 border-t">
                          <div className="text-xs text-gray-500">
                            Complaint #{complaint.complaintNumber || 'N/A'}
                          </div>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => {
                              setSelectedComplaintForView(complaint);
                              setShowComplaintDetailsDialog(true);
                            }}
                          >
                            View Details
                          </Button>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>

          <div className="flex justify-end">
            <Button variant="outline" onClick={() => setShowMyComplaintsDialog(false)}>
              Close
            </Button>
          </div>
        </DialogContent>
      </Dialog>

      {/* Enhanced Complaint Details Dialog for Branch Users */}
      <Dialog open={showComplaintDetailsDialog} onOpenChange={setShowComplaintDetailsDialog}>
        <DialogContent className="max-w-7xl max-h-[95vh] overflow-y-auto">
          <DialogHeader>
            <div className="flex justify-between items-start">
              <div>
                <DialogTitle className="text-xl">{selectedComplaintForView?.title}</DialogTitle>
                <DialogDescription>
                  Complaint #{selectedComplaintForView?.complaintId || selectedComplaintForView?.complaintNumber || selectedComplaintForView?.id} â€¢ Submitted on {formatComplaintDate(selectedComplaintForView?.createdAt, true)}
                </DialogDescription>
              </div>
              <div className="flex space-x-2">
                <Badge 
                  variant={selectedComplaintForView?.status === 'open' ? 'destructive' : 
                           selectedComplaintForView?.status === 'in-progress' ? 'default' : 
                           selectedComplaintForView?.status === 'resolved' ? 'secondary' : 'outline'}
                >
                  {selectedComplaintForView?.status?.replace('-', ' ') || 'Open'}
                </Badge>
                <Badge variant="outline">
                  {selectedComplaintForView?.priority || 'Medium'}
                </Badge>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => loadUserComplaints()}
                  className="text-xs"
                  data-testid="button-refresh-complaint"
                >
                  ðŸ”„ Refresh
                </Button>
              </div>
            </div>
          </DialogHeader>

          {selectedComplaintForView && (
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
              {/* Main Content */}
              <div className="lg:col-span-2 space-y-6">
                {/* Original Complaint Details */}
                <Card>
                  <CardHeader className="pb-3">
                    <CardTitle className="text-base flex items-center">
                      <FileTextIcon className="h-4 w-4 mr-2 text-blue-600" />
                      Original Complaint Details
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-4">
                    <div>
                      <Label className="text-sm font-medium text-gray-600">Description</Label>
                      <p className="mt-1 text-sm">{selectedComplaintForView.description}</p>
                    </div>

                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <Label className="text-xs font-medium text-gray-500">Category</Label>
                        <p className="capitalize">{selectedComplaintForView.category}</p>
                      </div>
                      <div>
                        <Label className="text-xs font-medium text-gray-500">Priority</Label>
                        <p className="capitalize">{selectedComplaintForView.priority}</p>
                      </div>
                      <div>
                        <Label className="text-xs font-medium text-gray-500">Location</Label>
                        <p>{selectedComplaintForView.location || 'Not specified'}</p>
                      </div>
                      <div>
                        <Label className="text-xs font-medium text-gray-500">Branch</Label>
                        <p>{selectedComplaintForView.branchName || 'Not specified'}</p>
                      </div>
                    </div>

                    {/* Original Photos */}
                    {selectedComplaintForView.photos && selectedComplaintForView.photos.length > 0 && (
                      <div>
                        <Label className="text-sm font-medium text-gray-600">
                          Original Photos ({selectedComplaintForView.photos.length})
                        </Label>
                        <div className="grid grid-cols-4 gap-2 mt-2">
                          {selectedComplaintForView.photos.map((photo: string, index: number) => (
                            <div key={index} className="relative group">
                              <img 
                                src={photo} 
                                alt={`Evidence ${index + 1}`}
                                className="w-full h-16 object-cover rounded border cursor-pointer hover:opacity-90 transition-opacity"
                                onClick={() => {
                                  setSelectedPhoto({
                                    url: photo,
                                    label: `Evidence ${index + 1}`
                                  });
                                  setShowPhotoModal(true);
                                }}
                                data-testid={`complaint-photo-${index}`}
                              />
                              <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-30 transition-all rounded flex items-center justify-center pointer-events-none">
                                <EyeIcon className="h-3 w-3 text-white opacity-0 group-hover:opacity-100 transition-opacity" />
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </CardContent>
                </Card>

                {/* Comments and Updates History */}
                <Card>
                  <CardHeader className="pb-3">
                    <CardTitle className="text-base flex items-center">
                      <MessageSquareIcon className="h-4 w-4 mr-2 text-green-600" />
                      Updates & Communication
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-4 max-h-80 overflow-y-auto">
                      {selectedComplaintForView.comments && selectedComplaintForView.comments.length > 0 ? (
                        selectedComplaintForView.comments
                          .sort((a: any, b: any) => {
                            const aTime = a.timestamp?.toDate ? a.timestamp.toDate() : new Date(a.timestamp || 0);
                            const bTime = b.timestamp?.toDate ? b.timestamp.toDate() : new Date(b.timestamp || 0);
                            return aTime.getTime() - bTime.getTime();
                          })
                          .map((comment: any, index: number) => (
                          <div key={comment.id || index} className="p-3 rounded-lg border-l-4 bg-gray-50 border-l-blue-500">
                            <div className="flex justify-between items-start mb-2">
                              <div className="flex items-center space-x-2">
                                <UserIcon className="h-4 w-4 text-gray-600" />
                                <span className="font-medium text-sm text-gray-900">
                                  {comment.author || 'System'}
                                </span>
                                {comment.type === 'status_change' && (
                                  <Badge variant="outline" className="text-xs">Status Update</Badge>
                                )}
                                {comment.type === 'document_upload' && (
                                  <Badge variant="outline" className="text-xs">Document Added</Badge>
                                )}
                              </div>
                              <span className="text-xs text-gray-500">
                                {comment.timestamp?.toDate ? 
                                  comment.timestamp.toDate().toLocaleString() : 
                                  new Date(comment.timestamp || 0).toLocaleString()
                                }
                              </span>
                            </div>
                            <p className="text-sm text-gray-700 leading-relaxed">{comment.text}</p>
                          </div>
                        ))
                      ) : (
                        <div className="text-center py-8 text-gray-500">
                          <MessageSquareIcon className="h-8 w-8 mx-auto mb-2 opacity-50" />
                          <p className="text-sm">No updates yet</p>
                          <p className="text-xs">Admin updates will appear here</p>
                        </div>
                      )}
                    </div>
                  </CardContent>
                </Card>
              </div>

              {/* Sidebar - Status & Documents */}
              <div className="space-y-6">
                {/* Status Information */}
                <Card>
                  <CardHeader className="pb-3">
                    <CardTitle className="text-base flex items-center">
                      <AlertTriangleIcon className="h-4 w-4 mr-2 text-orange-600" />
                      Status Information
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-3">
                    <div className="space-y-2">
                      <div className="flex justify-between">
                        <span className="text-sm text-gray-600">Current Status</span>
                        <Badge 
                          variant={selectedComplaintForView.status === 'open' ? 'destructive' : 
                                   selectedComplaintForView.status === 'in-progress' ? 'default' : 
                                   selectedComplaintForView.status === 'resolved' ? 'secondary' : 'outline'}
                          className="text-xs"
                        >
                          {selectedComplaintForView.status?.replace('-', ' ') || 'Open'}
                        </Badge>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-sm text-gray-600">Priority</span>
                        <Badge variant="outline" className="text-xs">
                          {selectedComplaintForView.priority || 'Medium'}
                        </Badge>
                      </div>
                      <div className="flex justify-between text-sm">
                        <span className="text-gray-600">Last Updated</span>
                        <span className="text-gray-900">
                          {selectedComplaintForView.lastUpdated?.toDate ? 
                            selectedComplaintForView.lastUpdated.toDate().toLocaleDateString() : 
                            selectedComplaintForView.updatedAt?.toDate ? 
                            selectedComplaintForView.updatedAt.toDate().toLocaleDateString() : 
                            'Unknown date'
                          }
                        </span>
                      </div>
                    </div>
                  </CardContent>
                </Card>

                {/* Resolution Information */}
                {(selectedComplaintForView.resolution || selectedComplaintForView.resolutionNotes || selectedComplaintForView.status === 'resolved' || selectedComplaintForView.status === 'closed') && (
                  <Card>
                    <CardHeader className="pb-3">
                      <CardTitle className="text-base flex items-center">
                        <ImageIcon className="h-4 w-4 mr-2 text-green-600" />
                        Resolution
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      {(selectedComplaintForView.resolution || selectedComplaintForView.resolutionNotes) ? (
                        <div className="p-3 bg-green-50 border border-green-200 rounded">
                          <p className="text-sm text-green-900">{selectedComplaintForView.resolution || selectedComplaintForView.resolutionNotes}</p>
                          {selectedComplaintForView.resolvedAt && (
                            <p className="text-xs text-green-700 mt-2">
                              Resolved on {selectedComplaintForView.resolvedAt.toDate ? 
                                selectedComplaintForView.resolvedAt.toDate().toLocaleString() : 'Unknown date'}
                            </p>
                          )}
                        </div>
                      ) : (
                        <p className="text-sm text-gray-600">Complaint marked as {selectedComplaintForView.status}</p>
                      )}
                    </CardContent>
                  </Card>
                )}

                {/* Documents Added by Admin */}
                {selectedComplaintForView.documents && selectedComplaintForView.documents.length > 0 && (
                  <Card>
                    <CardHeader className="pb-3">
                      <CardTitle className="text-base flex items-center">
                        <FileIcon className="h-4 w-4 mr-2 text-purple-600" />
                        Documents ({selectedComplaintForView.documents.length})
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      <div className="space-y-2">
                        {selectedComplaintForView.documents.map((doc: any, index: number) => (
                          <div key={doc.id || index} className="flex items-center justify-between p-2 border rounded hover:bg-gray-50">
                            <div className="flex items-center space-x-2 flex-1 min-w-0">
                              <FileIcon className="h-4 w-4 text-gray-500 flex-shrink-0" />
                              <div className="min-w-0 flex-1">
                                <p className="text-sm font-medium text-gray-900 truncate">
                                  {doc.name}
                                </p>
                                <p className="text-xs text-gray-500">
                                  Added by {doc.uploadedBy || 'Admin'}
                                </p>
                              </div>
                            </div>
                            <div className="flex items-center space-x-1 flex-shrink-0">
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => {
                                  if (doc.url) {
                                    window.open(doc.url, '_blank');
                                  }
                                }}
                                className="h-6 w-6 p-0"
                                data-testid={`button-view-document-${index}`}
                              >
                                <EyeIcon className="h-3 w-3" />
                              </Button>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => {
                                  if (doc.url) {
                                    const link = document.createElement('a');
                                    link.href = doc.url;
                                    link.download = doc.name;
                                    document.body.appendChild(link);
                                    link.click();
                                    document.body.removeChild(link);
                                  }
                                }}
                                className="h-6 w-6 p-0"
                                data-testid={`button-download-document-${index}`}
                              >
                                <DownloadIcon className="h-3 w-3" />
                              </Button>
                            </div>
                          </div>
                        ))}
                      </div>
                    </CardContent>
                  </Card>
                )}
              </div>
            </div>
          )}

          <div className="flex justify-end">
            <Button variant="outline" onClick={() => setShowComplaintDetailsDialog(false)}>
              Close
            </Button>
          </div>
        </DialogContent>
      </Dialog>

      {/* My Update Logs Dialog */}
      <Dialog open={showMyLogsDialog} onOpenChange={setShowMyLogsDialog}>
        <DialogContent className="sm:max-w-4xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>My Tank Update History ({myUpdateLogs.length})</DialogTitle>
            <DialogDescription>
              Complete log of all your oil tank level updates with photos and details
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4">
            {myUpdateLogs.length === 0 ? (
              <div className="text-center py-8">
                <HistoryIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-600 mb-2">No tank updates found</p>
                <p className="text-sm text-gray-500">Your tank level updates will appear here</p>
              </div>
            ) : (
              <div className="space-y-4">
                {myUpdateLogs.map((log, index) => (
                  <Card key={log.id || index} className="border-l-4 border-l-blue-500">
                    <CardHeader className="pb-2">
                      <div className="flex justify-between items-start">
                        <div>
                          <CardTitle className="text-lg flex items-center gap-2">
                            <DropletIcon className="h-5 w-5 text-blue-600" />
                            {log.branchName} - {log.oilTypeName}
                          </CardTitle>
                          <CardDescription>
                            {log.updatedAt ? new Date(log.updatedAt).toLocaleString() : 'Unknown date'}
                          </CardDescription>
                        </div>
                        <Badge variant="outline">
                          {log.previousLevel}L â†’ {log.newLevel}L
                        </Badge>
                      </div>
                    </CardHeader>
                    <CardContent>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div className="space-y-2">
                          <p className="text-sm"><strong>Previous Level:</strong> {log.previousLevel?.toLocaleString()}L</p>
                          <p className="text-sm"><strong>New Level:</strong> {log.newLevel?.toLocaleString()}L</p>
                          <p className="text-sm"><strong>Change:</strong> 
                            <span className={`ml-1 font-medium ${
                              (log.newLevel - log.previousLevel) > 0 ? 'text-green-600' : 'text-red-600'
                            }`}>
                              {(log.newLevel - log.previousLevel) > 0 ? '+' : ''}{(log.newLevel - log.previousLevel)?.toLocaleString()}L
                            </span>
                          </p>
                          {log.notes && (
                            <p className="text-sm"><strong>Notes:</strong> {log.notes}</p>
                          )}
                        </div>

                        {/* Photos */}
                        <div className="space-y-2">
                          <h4 className="font-medium text-sm">Photo Evidence:</h4>
                          <div className="grid grid-cols-2 gap-2">
                            {log.photos?.gaugePhoto && (
                              <div className="text-center">
                                <img 
                                  src={log.photos.gaugePhoto} 
                                  alt="Tank Gauge"
                                  className="w-full h-20 object-cover rounded cursor-pointer border hover:opacity-80 transition-opacity"
                                  onClick={() => {
                                    setSelectedPhoto({url: log.photos.gaugePhoto, label: 'Tank Gauge Photo'});
                                    setShowPhotoModal(true);
                                  }}
                                />
                                <p className="text-xs text-gray-500 mt-1">Tank Gauge</p>
                              </div>
                            )}
                            {log.photos?.systemPhoto && (
                              <div className="text-center">
                                <img 
                                  src={log.photos.systemPhoto} 
                                  alt="System Screen"
                                  className="w-full h-20 object-cover rounded cursor-pointer border hover:opacity-80 transition-opacity"
                                  onClick={() => {
                                    setSelectedPhoto({url: log.photos.systemPhoto, label: 'System Screen Photo'});
                                    setShowPhotoModal(true);
                                  }}
                                />
                                <p className="text-xs text-gray-500 mt-1">System Screen</p>
                              </div>
                            )}
                          </div>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>
        </DialogContent>
      </Dialog>

      {/* Conflict Resolution Dialog for Concurrent Updates */}
      <Dialog open={conflictDialogOpen} onOpenChange={setConflictDialogOpen}>
        <DialogContent className="sm:max-w-[500px]">
          <DialogHeader>
            <DialogTitle className="flex items-center space-x-2">
              <AlertTriangleIcon className="h-5 w-5 text-yellow-500" />
              <span>Update Conflict Detected</span>
            </DialogTitle>
            <DialogDescription>
              Another user updated this tank while you were making changes. Please review the conflict and choose how to proceed.
            </DialogDescription>
          </DialogHeader>

          <div className="space-y-4">
            {conflictDetails && (
              <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 space-y-3">
                <div className="flex justify-between items-center">
                  <span className="text-sm font-medium text-gray-700">Server Level:</span>
                  <span className="text-lg font-bold text-blue-600">
                    {conflictDetails.serverLevel?.toLocaleString()}L
                  </span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm font-medium text-gray-700">Updated By:</span>
                  <span className="text-sm text-gray-900">
                    {conflictDetails.serverUpdatedBy || 'Another user'}
                  </span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm font-medium text-gray-700">Updated At:</span>
                  <span className="text-sm text-gray-600">
                    {conflictDetails.serverUpdatedAt ? 
                      new Date(conflictDetails.serverUpdatedAt).toLocaleString() : 
                      'Just now'
                    }
                  </span>
                </div>
              </div>
            )}

            <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
              <div className="flex justify-between items-center">
                <span className="text-sm font-medium text-gray-700">Your Intended Level:</span>
                <span className="text-lg font-bold text-green-600">
                  {retryUpdateData?.newLevel?.toLocaleString()}L
                </span>
              </div>
            </div>
          </div>

          <DialogFooter className="flex space-x-2">
            <Button 
              variant="outline" 
              onClick={() => {
                setConflictDialogOpen(false);
                setConflictDetails(null);
                setRetryUpdateData(null);
              }}
            >
              Cancel
            </Button>
            <Button 
              variant="secondary"
              onClick={() => {
                setConflictDialogOpen(false);
                setConflictDetails(null);
                setRetryUpdateData(null);
                setShowUpdateDialog(false);
                setUpdateStep('branch');
                // Reset form without triggering page reload
                setSelectedBranchForUpdate('');
                setSelectedTankForUpdate('');
                setGaugePhoto(null);
                setSystemPhoto(null);
                setGaugePhotoPreview('');
                setSystemPhotoPreview('');
                setManualQuantity('');
                setUpdateNotes('');
              }}
            >
              Start Over
            </Button>
            <Button 
              onClick={() => {
                if (retryUpdateData) {
                  setConflictDialogOpen(false);
                  // Retry with the original data - no page reload needed
                  setManualQuantity(retryUpdateData.newLevel.toString());
                  if (retryUpdateData.photos?.gauge) setGaugePhoto(retryUpdateData.photos.gauge);
                  if (retryUpdateData.photos?.system) setSystemPhoto(retryUpdateData.photos.system);

                  // Retry immediately without loadData() to prevent loading screen
                  setTimeout(() => {
                    handleSubmitTankUpdate();
                  }, 100);
                }
                setConflictDetails(null);
                setRetryUpdateData(null);
              }}
              className="bg-blue-600 hover:bg-blue-700"
            >
              Retry Update
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Footer with Creator Credit */}
      <div className="mt-8 border-t border-gray-200 pt-4 text-center">
        <p className="text-xs text-gray-500">Created by Asif Shaikh</p>
      </div>
    </div>
  );
}

