                            <div className="text-xs mb-1 flex items-center gap-1">
                              <Clock className="h-3 w-3 text-gray-400" />
                              <span className="text-gray-500">Last updated:</span>
                              <span className={`font-medium ${lastUpdateColor}`}>{lastUpdateText}</span>
                            </div>
